let cardUrl=new URL(window.location.href);let paramsName=cardUrl.searchParams.get("name");const title=document.querySelector(".title");const clickBox=document.querySelector(".clickBox");const count=document.querySelector(".count");const clickImg=document.querySelector(".image");let countNum=0;clickBox.addEventListener("click",userClick);clickBox.addEventListener("mousedown",userMousedown);clickBox.addEventListener("mouseup",userMouseup);window.onload=async function(){if(paramsName!=null){loadJsonData()}};let pageData="";let audioObj="";let audioTime=0;const host=window.location.protocol+"//"+window.location.host;const path=window.location.pathname.replace("/page/click.html","");function loadJsonData(){pageData=cardData.filter(e=>e.filename===paramsName)[0];const cardPath=`/src/${pageData.filename}-a.png`;clickImg.src=host+path+cardPath;clickImg.ondragstart=()=>{return false};title.textContent=pageData.name;const audioPath=`/src/${pageData.filename}.mp3`;audioObj=new Audio(host+path+audioPath);audioObj.currentTime=audioTime}function userClick(e){}function userMousedown(){countNum+=1;count.textContent=countNum;audioObj.play();const cardPath=`/src/${pageData.filename}-b.png`;clickImg.src=host+path+cardPath}function userMouseup(){if(audioObj.paused===false){audioObj.pause();audioObj.currentTime=audioTime}const cardPath=`/src/${pageData.filename}-a.png`;clickImg.src=host+path+cardPath}