window.onload=async function(){addItem()};function addItem(){try{const o=document.querySelector("body");const c=150;cardData.forEach((e,n)=>{setTimeout(()=>{const n=document.createElement("div");n.className="cardBox";n.onclick=()=>{cardClick(e.filename)};const c=document.createElement("img");c.src=`./src/${e.filename}-a.png`;const t=document.createElement("div");t.className="cardBody";t.textContent=e.name;n.appendChild(c);n.appendChild(t);o.appendChild(n)},n*(c-cardData.length))})}catch(n){alert("檔案錯誤！")}}function cardClick(n){const c=window.location.protocol+"//"+window.location.host;const t=window.location.pathname.replace("/index.html","");const e=`/page/click.html?name=${n}`;window.location.href=c+t+e}