window.onload=async function(){addItem()};function addItem(){try{const o=document.querySelector("body");cardData.forEach((t,n)=>{setTimeout(()=>{const n=document.createElement("div");n.className="cardBox";n.onclick=()=>{cardClick(t.filename)};const c=document.createElement("img");c.src=`./src/${t.filename}-a.png`;const e=document.createElement("div");e.className="cardBody";e.textContent=t.name;n.appendChild(c);n.appendChild(e);o.appendChild(n)},n*250)})}catch(n){alert("檔案錯誤！")}}function cardClick(n){const c=window.location.protocol+"//"+window.location.host;const e=window.location.pathname.replace("/index.html","");const t=`/page/click.html?name=${n}`;window.location.href=c+e+t}