window.onload=async function(){addItem()};function addItem(){try{const body=document.querySelector("body");cardData.forEach((elem,index)=>{setTimeout(()=>{const card=document.createElement("div");card.className="cardBox";card.onclick=()=>{cardClick(elem.filename)};const cardImg=document.createElement("img");cardImg.src=`./src/${elem.filename}-a.png`;const cardContent=document.createElement("div");cardContent.className="cardBody";cardContent.textContent=elem.name;card.appendChild(cardImg);card.appendChild(cardContent);body.appendChild(card)},index*250)})}catch(e){alert("檔案錯誤！")}}function cardClick(item){const host=window.location.protocol+"//"+window.location.host;const path=window.location.pathname.replace("/index.html","");const cardPath=`/page/click.html?name=${item}`;window.location.href=host+path+cardPath}